import { BasePageFrontMatter, ConvertLocaleConfig } from 'vuepress-shared';
import { Page, LocaleConfig, PluginFunction } from '@vuepress/core';

interface CopyrightPluginFrontmatter extends BasePageFrontMatter {
    copy?: {
        /**
         * Min words triggering copyright append
         *
         * 触发附加版权的最小字数
         *
         * @default 100
         */
        triggerWords?: number;
        /**
         * Disable copy
         *
         * 禁用复制
         *
         * @default false
         */
        disableCopy?: boolean;
        /**
         * Disable selection
         *
         * 禁用选择
         *
         * @default false
         */
        disableSelection?: boolean;
    } | boolean;
}

interface CopyrightLocaleData {
    /**
     * Author text
     *
     * @description `:author` will be replaced by author
     *
     * 作者文字
     *
     * @description `:author` 将会被作者替换
     */
    author: string;
    /**
     * License text
     *
     * @description `:license` will be replaced by current license
     *
     * 协议文字
     *
     * @description `:license` 会被当前协议替换
     */
    license: string;
    /**
     * Link text
     *
     * @description `:url` will be replaced by current page link
     *
     * 链接文字
     *
     * @description `:url` 会替换为当前页面链接
     */
    link: string;
}
type CopyrightLocaleConfig = ConvertLocaleConfig<CopyrightLocaleData>;

/** Multi language config for copyright */
declare const copyrightLocales: CopyrightLocaleConfig;

interface CopyrightOptions {
    /**
     * Deploy hostname
     *
     * 部署的域名
     */
    hostname?: string;
    /**
     * Author Information
     *
     * 作者信息
     */
    author?: string | (<ExtraPageData extends Record<string | number | symbol, unknown> = Record<never, never>, ExtraPageFrontmatter extends Record<string | number | symbol, unknown> = Record<string, unknown>, ExtraPageFields extends Record<string | number | symbol, unknown> = Record<never, never>>(page: Page<ExtraPageData, ExtraPageFrontmatter, ExtraPageFields>) => string);
    /**
     * License Information
     *
     * 协议信息
     */
    license?: string | (<ExtraPageData extends Record<string | number | symbol, unknown> = Record<never, never>, ExtraPageFrontmatter extends Record<string | number | symbol, unknown> = Record<string, unknown>, ExtraPageFields extends Record<string | number | symbol, unknown> = Record<never, never>>(page: Page<ExtraPageData, ExtraPageFrontmatter, ExtraPageFields>) => string);
    /**
     * Min words triggering copyright append
     *
     * 触发附加版权的最小字数
     *
     * @default 100
     */
    triggerWords?: number;
    /**
     * Whether enabled globally
     *
     * 是否全局启用
     *
     * @default false
     */
    global?: boolean;
    /**
     * Disable copy
     *
     * 禁用复制
     *
     * @default false
     */
    disableCopy?: boolean;
    /**
     * Disable selection
     *
     * 禁用选择
     *
     * @default false
     */
    disableSelection?: boolean;
    /**
     * Locales config for copyright
     *
     * 复制版权的多语言配置
     */
    locales?: LocaleConfig<CopyrightLocaleData>;
}

declare const copyrightPlugin: (options: CopyrightOptions) => PluginFunction;

export { CopyrightLocaleConfig, CopyrightLocaleData, CopyrightOptions, CopyrightPluginFrontmatter, copyrightLocales, copyrightPlugin };
